import React, { useState, useEffect } from 'react'
import { useDispatch, useSelectio, useSelector } from 'react-redux'
import { setTodoList, addTodo, updateTodo, toggleCompleted } from '../features/todoSlice'
import './ addTaskForm.css'
import './edit.css'
import './task.css'
import './edit.css'
import { PlusIcon } from '@heroicons/react/16/solid'
import { CheckIcon } from '@heroicons/react/16/solid'
import { PencilSquareIcon, TrashIcon, CheckCircleIcon, enterEditMode } from '@heroicons/react/16/solid'


function TodoList() {

    const dispatch = useDispatch();
    const todoList = useSelector((state) => state.todo.todoList)

    const [currentTodo, setCurrentTodo] = useState(null)
    const [newTask, setNewTask] = useState('')

    useEffect(() => {
        if (todoList.length > 0) {
            localStorage.setItem('todolist', JSON.stringify(todoList));
        }
    }, [todoList]);
    useEffect(() => {
        const localTodoList = JSON.parse(localStorage.getItem('todoList'));
        if (localTodoList) {
            dispatch(setTodoList(localTodoList))
        }
    },
        []);


    const handleAddTodo = (task) => {
        if (task.trim().length === 0) {
            alert('please enter a task');
        } else {
            dispatch(addTodo({
                task: task,
                id: Date.now(),
            }));
            setNewTask('')

        }
    }

    const handleUpdateTodoList = (id, task) => {
        if (task.trim().length === 0) {
            alert('please enter a task');
        } else {
            dispatch(updateTodo({
                task: task,
                id: id,
            }));

        }
    }

    const handleDeleteTodo = (id) => {
        const updateTodoList = todoList.filter((todo) =>
            todo.id != id);
        dispatch(setTodoList(updateTodoList));
        localStorage.setItem('todoList', JSON.stringify(updateTodoList));
    }


    const handleToggleComleted = (id) => {
        dispatch(toggleCompleted({ id }))
    }
    return (

        <div>
            <div className="todo">
                <div className="add-task-input">
                    <input
                        type='text'
                        className="input"
                        value={newTask}
                        onChange={(e) => setNewTask(e.target.value)}
                        placeholder="Enter Task"
                    />
                    <div>
                        <button
                            className="btn"
                            onClick={() => {
                                handleAddTodo(newTask)
                            }}
                        >
                            <PlusIcon className='plusIcon' strokeWidth={1.2} />
                        </button>
                    </div>
                </div>
            </div>
            {/* Edit Task */}
            {currentTodo &&
                <>
                    <div className="edit-form">
                        <input
                            type='text'
                            className="input"
                            value={currentTodo}
                            onChange={(e) => setCurrentTodo(e.target.value)}
                            placeholder="Enter Task"
                        />
                    </div>
                    <button
                        className="edit-btn"
                        onClick={() => {
                            handleUpdateTodoList(currentTodo.id, newTask)
                        }}
                    >
                        < CheckIcon className='edit-plusIcon' strokeWidth={1.2} />
                    </button>
                </>

            }

            <div>
                {todoList.length === 0 ?
                    (<>
                        <div >
                            <p >You have no todo's please add one</p>
                        </div>
                    </>) :
                    (<>
                        {todoList.map((todo) => (
                            <div key={todo.id}>
                                <div className={todo.completed ? ' item done' : 'taskUndone item'}>
                                    <div className='task' onClick={() => { handleToggleComleted(todo.id) }}>
                                        <CheckCircleIcon widths={24} height={24} className={todo.completed ? 'checkIcon checked ' : 'checkIcon notChecked '} />
                                        <span style={{ 'textDecorationLine': todo.completed ? 'line-through' : 'none' }}  > {todo.task} </span>
                                    </div>
                                    <div className="edittingIcons">
                                        <button
                                            className='edit'
                                            onClick={() => {
                                                setCurrentTodo(todo)
                                                // setNewTask(todo.task)
                                            }}
                                        >
                                            <PencilSquareIcon widths={24} height={24} className="editIcon Icon" />
                                        </button>
                                        <button
                                            className='delete'
                                            onClick={() => handleDeleteTodo(todo.id)} >
                                            <TrashIcon widths={24} height={24} className="trashIcon Icon" />
                                        </button>
                                    </div>
                                </div>
                            </div>
                        ))}





                        <div >

                            <div>

                                {todoList.map((todo) => (
                                    <div key={todo.id}>
                                        <div
                                            className={`${todo.complete ? 'line-through text-greenTeal' : 'text-sunsetOrange'}`}
                                            onClick={() => { handleToggleComleted(todo.id) }}>
                                            {todo.task}</div>
                                        <div>
                                            <button
                                                onClick={() => {
                                                    setCurrentTodo(todo)
                                                    setNewTask(todo.task)
                                                }
                                                }>
                                                {/* <TiPencil /> */}
                                            </button>
                                            <button
                                                onClick={() => handleDeleteTodo(todo.id)}>
                                                {/* <BsTrash /> */}
                                            </button>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </>
                    )}
            </div>

        </div >
    )
}

export default TodoList
