import React, { useState, useEffect } from 'react'
import { useDispatch, useSelectio, useSelector } from 'react-redux'
import { setTodoList, addTodo, updateTodo, toggleCompleted } from '../features/todoSlice'



function TodoList() {

    const dispatch = useDispatch();
    const todoList = useSelector((state) => state.todo.todoList)

    const [currentTodo, setCurrentTodo] = useState(null)
    const [newTask, setNewTask] = useState('')

    useEffect(() => {
        if (todoList.length > 0) {
            localStorage.setItem('todolist', JSON.stringify(todoList));
        }
    }, [todoList]);
    useEffect(() => {
        const localTodoList = JSON.parse(localStorage.getItem('todoList'));
        if (localTodoList) {
            dispatch(setTodoList(localTodoList))
        }
    },
        []);


    const handleAddTodo = (task) => {
        if (task.trim().length === 0) {
            alert('please enter a task');
        } else {
            dispatch(addTodo({
                task: task,
                id: Date.now(),
            }));
            setNewTask('')

        }
    }

    const handleUpdateTodoList = (id, task) => {
        if (task.trim().length === 0) {
            alert('please enter a task');
        } else {
            dispatch(updateTodo({
                task: task,
                id: id,
            }));

        }
    }

    const handleDeleteTodo = (id) => {
        const updateTodoList = todoList.filter((todo) =>
            todo.id != id);
        dispatch(setTodoList(updateTodoList));
        localStorage.setItem('todoList', JSON.stringify(updateTodoList));
    }


    const handleToggleComleted = (id) => {
        dispatch(toggleCompleted({ id }))
    }
    return (
        <div>

            <div>
                <div>
                    <input
                        type='text'

                        value={newTask}
                        onChange={(e) => setNewTask(e.target.value)}

                    />
                    <div>

                        <>
                            <button
                                onClick={() => {
                                    handleUpdateTodoList(currentTodo.id, newTask)
                                }}
                            >
                                Save
                            </button>
                            <button

                                onClick={() => setShowModal(false)}>
                                Cancel
                            </button>
                        </>

                        <>
                            <button

                                onClick={() => setShowModal(false)}
                            >
                                Cancel
                            </button>
                            <button

                                onClick={() => {
                                    handleAddTodo(newTask)

                                }}
                            >
                                Add
                            </button>
                        </>
                    </div>
                </div>
            </div>

            <div>
                {/* {todoList.length === 0 ? */}
                {/* (<> */}
                <div className='mb-8'>
                    <div className='sm:w-[500px] sm:h-[500px] min-w-[250px]'>
                        <img src={empty} alt='img' />
                    </div>
                    <p className='text-center text-Gray' >You have no todo's please add one</p>
                </div>
                {/* </>) : ( */}
                <div className='container mx-auto mt-6' >
                    <div className='flex justify-center mb-6'>
                        <select
                            onChange={e => handleSort(e.target.value)}
                            className='p-1 outline-none text-sm' >
                            <option
                                value='All'
                                className='text-sm'>All </option>
                            <option
                                value='Completed'
                                className='text-sm'>Completed</option>
                            <option
                                value='Not Completed'
                                className='text-sm'>Not Completed</option>
                        </select>
                    </div>
                    <div>
                        {/* {sortTodoList.map((todo) => ( */}
                        <div key={todo.id}>
                            <div
                                className={`${todo.complete ? 'line-through text-greenTeal' : 'text-sunsetOrange'}`}
                                onClick={() => { handleToggleComleted(todo.id) }}>
                                {todo.task}</div>
                            <div>
                                <button
                                    onClick={() => {
                                        setCurrentTodo(todo)
                                        setNewTask(todo.task)
